const tabsBtn=document.querySelectorAll(".tabs__nav-btn"),tabsItems=document.querySelectorAll(".tabs__item");function onTabClick(t){t.addEventListener("click",(function(){let e=t,c=e.getAttribute("data-tab"),a=document.querySelector(c);e.classList.contains("active")||(tabsBtn.forEach((function(t){t.classList.remove("active")})),tabsItems.forEach((function(t){t.classList.remove("active")})),e.classList.add("active"),a.classList.add("active"))}))}tabsBtn.forEach(onTabClick),document.querySelector(".tabs__nav-btn").click();